import React, { useState, useEffect } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);

  const [characterX, setCharacterX] = useState(100); // 캐릭터 위치
  const [bgOffset, setBgOffset] = useState(0);       // 배경 이동

  const playStartSound = () => {
    const audio = new Audio(startSound);
    audio.play();
  };

  const handleStartGame = () => {
    playStartSound();
    setShowGameContent(true);
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  // 방향키 이벤트
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "ArrowRight") {
        setCharacterX((prev) => Math.min(prev + 10, 300));
        setBgOffset((prev) => prev - 10);
      } else if (e.key === "ArrowLeft") {
        setCharacterX((prev) => Math.max(prev - 10, 0));
        setBgOffset((prev) => prev + 10);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="App">
      {/* 반짝이 하트 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {/* 홈 화면 */}
      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {/* 퀴즈 화면 */}
      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>퀴즈 게임</span>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>🗖</button>
                <button onClick={() => setScreen("home")}>❌</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  🎮 퀴즈 게임 시작
                </h2>
              </div>
            ) : (
              <div className="game-content">
                {/* 움직이는 배경 */}
                <img
                  src={ground}
                  className="game-background"
                  alt="bg"
                  style={{ transform: `translateX(${bgOffset}px)` }}
                />
                {/* 캐릭터 */}
                <img
                  src={fairy}
                  className="fairy-character"
                  alt="fairy"
                  style={{ left: `${characterX}px` }}
                />
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
