import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import hhhh from "./image/hhhh.png";

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [showStartScreen, setShowStartScreen] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const audioRef = useRef(null); // 🎵 효과음용 ref

  const toggleContent = () => setShowGameContent((prev) => !prev);
  const toggleFullscreen = () => setIsFullscreen((prev) => !prev);

  const handleHeartClick = () => {
    if (audioRef.current) {
      audioRef.current.play(); // 🎵 소리 재생
    }
    setScreen("quiz");
    setShowStartScreen(true);
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      <audio ref={audioRef} src="/sound/click.mp3" />

      {/* 반짝이 하트들 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={handleHeartClick}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`computer-window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header" onClick={toggleContent}>
              <span className="window-title">💻 퀴즈 타임</span>
              <div className="window-icons">
                <span className="icon" onClick={toggleContent}>🗕</span>
                <span className="icon" onClick={toggleFullscreen}>🗖</span>
                <span className="icon" onClick={() => setScreen("home")}>❌</span>
              </div>
            </div>

            {showStartScreen && !showGameContent && (
              <div className="start-screen">
                <img src={hhhh} alt="start" className="start-icon" onClick={() => setShowGameContent(true)} />
              </div>
            )}

            {showGameContent && (
              <div className="game-content">
                <img src={ground} alt="background" className="game-background" />
                <img src={fairy} alt="fairy" className="fairy-character" />
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
