import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";
import bgMusicFile from "./sounds/game back.wav";
import jumpSoundFile from "./sounds/game.mp3.wav";

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [fairyX, setFairyX] = useState(100);
  const [fairyY, setFairyY] = useState(0);
  const [bgOffset, setBgOffset] = useState(0);
  const [quizHearts, setQuizHearts] = useState([]);
  const [balloonText, setBalloonText] = useState("");
  const [isJumping, setIsJumping] = useState(false);
  const [musicPlaying, setMusicPlaying] = useState(false);

  const bgMusicRef = useRef(null);
  const jumpSoundRef = useRef(null);

  const playStartSound = () => new Audio(startSound).play();
  const playJumpSound = () => jumpSoundRef.current && jumpSoundRef.current.play();
  const playBgMusic = () => {
    if (bgMusicRef.current) {
      bgMusicRef.current.loop = true;
      bgMusicRef.current.play();
    }
  };
  const stopBgMusic = () => {
    if (bgMusicRef.current) {
      bgMusicRef.current.pause();
      bgMusicRef.current.currentTime = 0;
    }
  };

  const toggleMusic = () => {
    if (musicPlaying) {
      stopBgMusic();
    } else {
      playBgMusic();
    }
    setMusicPlaying(!musicPlaying);
  };

  const checkHeartCollision = (newX, newBgOffset) => {
    setQuizHearts((prevHearts) =>
      prevHearts.map((heart) => {
        const heartX = heart.left + newBgOffset;
        const diff = Math.abs(newX - heartX);
        if (!heart.found && diff < 40) {
          new Audio(jumpSoundFile).play();
          setBalloonText("Ïù¥Í±∏ ÎßûÏ∂∞Î¥ê!");
          return { ...heart, found: true };
        }
        return heart;
      })
    );
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  useEffect(() => {
    const hearts = Array.from({ length: 5 }, (_, i) => ({
      id: i,
      left: 300 + i * 200,
      top: 250,
      found: false,
    }));
    setQuizHearts(hearts);
    setFairyX(100);
    setFairyY(0);
    setBgOffset(0);
    setBalloonText("");
    setIsJumping(false);
  }, [showGameContent]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!showGameContent) return;

      let newX = fairyX;
      let newBgOffset = bgOffset;

      if (e.key === "ArrowRight") {
        newX += 20;
        newBgOffset -= 20;
      } else if (e.key === "ArrowLeft") {
        newX -= 20;
        newBgOffset += 20;
      } else if (e.key === " ") {
        if (!isJumping) {
          setIsJumping(true);
          setFairyY(-50);
          playJumpSound();
          setTimeout(() => {
            setFairyY(0);
            setIsJumping(false);
          }, 500);
        }
      }

      setFairyX(newX);
      setBgOffset(newBgOffset);
      checkHeartCollision(newX, newBgOffset);
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [fairyX, bgOffset, showGameContent, isJumping]);

  return (
    <div className="App">
      <audio ref={bgMusicRef} src={bgMusicFile} />
      <audio ref={jumpSoundRef} src={jumpSoundFile} />

      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">ÌïòÌä∏Î•º ÎàåÎü¨Ï§ò</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>ÌÄ¥Ï¶à Í≤åÏûÑ</span>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>üóñ</button>
                <button onClick={() => setScreen("home")}>‚ùå</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  üéÆ ÌÄ¥Ï¶à Í≤åÏûÑ ÏãúÏûë
                </h2>
                <button className="music-btn" onClick={toggleMusic}>
                  {musicPlaying ? "üîá Î∞∞Í≤Ω ÏùåÏïÖ ÎÅÑÍ∏∞" : "üîä Î∞∞Í≤Ω ÏùåÏïÖ ÏºúÍ∏∞"}
                </button>
              </div>
            ) : (
              <div className="game-content">
                <img
                  src={ground}
                  className="game-background"
                  style={{ left: `${bgOffset}px` }}
                  alt="bg"
                />
                {quizHearts.map(
                  (heart) =>
                    !heart.found && (
                      <img
                        key={heart.id}
                        src={jujinHeart}
                        className="heart-item"
                        alt="heart"
                        style={{
                          left: `${heart.left + bgOffset}px`,
                          top: `${heart.top}px`,
                        }}
                      />
                    )
                )}
                <img
                  src={fairy}
                  className="fairy-character"
                  alt="fairy"
                  style={{ left: `${fairyX}px`, bottom: `${fairyY}px` }}
                />
                {balloonText && (
                  <div className="quiz-balloon" style={{ left: `${fairyX}px` }}>
                    {balloonText}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
