import React, { useState, useEffect } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";

const quizList = [
  "내가 제일 좋아하는 음식은?",
  "우리가 처음 만난 날은 언제일까?",
  "내가 가장 좋아하는 계절은?",
  "마지막으로, 내가 너에게 하고 싶은 말은? 💖",
];

function App() {
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [fairyPosition, setFairyPosition] = useState({ x: 100, y: 300 });
  const [collectedHearts, setCollectedHearts] = useState([]);
  const [quizIndex, setQuizIndex] = useState(0);

  const playStartSound = () => {
    const audio = new Audio(startSound);
    audio.play();
  };

  const handleStartGame = () => {
    playStartSound();
    setShowGameContent(true);
  };

  const heartPositions = [
    { id: 0, x: 200, y: 200 },
    { id: 1, x: 400, y: 250 },
    { id: 2, x: 600, y: 200 },
    { id: 3, x: 300, y: 400 },
  ];

  useEffect(() => {
    const handleKeyDown = (e) => {
      setFairyPosition((prev) => {
        const speed = 20;
        let { x, y } = prev;
        if (e.key === "ArrowLeft") x -= speed;
        if (e.key === "ArrowRight") x += speed;
        if (e.key === "ArrowUp") y -= speed;
        if (e.key === "ArrowDown") y += speed;
        return { x: Math.max(0, Math.min(x, 700)), y: Math.max(0, Math.min(y, 500)) };
      });
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  useEffect(() => {
    heartPositions.forEach((heart) => {
      if (!collectedHearts.includes(heart.id)) {
        const dx = fairyPosition.x - heart.x;
        const dy = fairyPosition.y - heart.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        if (distance < 40) {
          setCollectedHearts((prev) => [...prev, heart.id]);
        }
      }
    });
  }, [fairyPosition]);

  return (
    <div className="App">
      {screen === "home" && (
        <div className="home-screen">
          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </div>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>퀴즈 게임</span>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>🗖</button>
                <button onClick={() => setScreen("home")}>❌</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  🎮 퀴즈 게임 시작
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img src={ground} className="game-background" alt="bg" />
                <img
                  src={fairy}
                  className="fairy-character"
                  alt="fairy"
                  style={{ left: fairyPosition.x, top: fairyPosition.y }}
                />
                {heartPositions.map(
                  (heart) =>
                    !collectedHearts.includes(heart.id) && (
                      <img
                        key={heart.id}
                        src={jujinHeart}
                        className="heart-item"
                        alt="heart"
                        style={{ left: heart.x, top: heart.y }}
                      />
                    )
                )}
                {collectedHearts.includes(quizIndex) && (
                  <div className="quiz-bubble" style={{ left: fairyPosition.x, top: fairyPosition.y - 60 }}>
                    <p>{quizList[quizIndex]}</p>
                    {quizIndex < quizList.length - 1 && (
                      <button onClick={() => setQuizIndex((prev) => prev + 1)}>다음</button>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
