import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";
import eatSound from "./sounds/game.mp3.wav";
import bgMusicFile from "./sounds/game back.wav";

function App() {
  const [screen, setScreen] = useState("home");
  const [showGame, setShowGame] = useState(false);
  const [homeHearts, setHomeHearts] = useState([]);
  const [quizHearts, setQuizHearts] = useState([]);
  const [fairyX, setFairyX] = useState(100);
  const [fairyY, setFairyY] = useState(0);
  const [bgOffset, setBgOffset] = useState(0);
  const [balloonText, setBalloonText] = useState("");
  const [isJumping, setIsJumping] = useState(false);
  const [muted, setMuted] = useState(false);

  const bgmRef = useRef(null);

  // 초기 설정
  useEffect(() => {
    setHomeHearts(
      Array.from({ length: 15 }, (_, i) => ({
        id: i,
        left: Math.random() * 100,
        size: 20 + Math.random() * 30,
        duration: 5 + Math.random() * 5,
      }))
    );
    bgmRef.current = new Audio(bgMusicFile);
    bgmRef.current.loop = true;
    bgmRef.current.volume = 0.3;
  }, []);

  // 퀴즈 하트 초기화
  useEffect(() => {
    if (!showGame) return;
    setQuizHearts(
      Array.from({ length: 5 }, (_, i) => ({
        id: i,
        left: 300 + i * 200,
        top: 250,
        found: false,
      }))
    );
    setFairyX(100);
    setFairyY(0);
    setBgOffset(0);
    setBalloonText("");
    setIsJumping(false);
  }, [showGame]);

  // 키컨트롤 & 충돌
  useEffect(() => {
    const onKey = (e) => {
      if (screen !== "quiz" || !showGame) return;
      let x = fairyX,
        b = bgOffset;
      if (e.key === "ArrowRight") {
        x += 20; b -= 20;
      } else if (e.key === "ArrowLeft") {
        x -= 20; b += 20;
      } else if (e.key === " " && !isJumping) {
        setIsJumping(true);
        let h = 0;
        const up = setInterval(() => {
          h += 5; setFairyY(h);
          if (h >= 50) {
            clearInterval(up);
            const down = setInterval(() => {
              h -= 5; setFairyY(h);
              if (h <= 0) {
                clearInterval(down);
                setIsJumping(false);
              }
            }, 20);
          }
        }, 20);
        new Audio(startSound).play();
      }
      setFairyX(x); setBgOffset(b);
      setQuizHearts((prev) =>
        prev.map((h) => {
          if (!h.found && Math.abs(x - (h.left + b)) < 40) {
            new Audio(eatSound).play();
            setBalloonText("이걸 맞춰봐!");
            return { ...h, found: true };
          }
          return h;
        })
      );
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [screen, showGame, fairyX, bgOffset, isJumping]);

  // 시작
  const startGame = () => {
    new Audio(startSound).play();
    if (!muted) bgmRef.current.play().catch(() => {});
    setShowGame(true);
  };

  // 음소거 토글
  const toggleMute = () => {
    setMuted((m) => {
      const next = !m;
      if (next) bgmRef.current.pause();
      else bgmRef.current.play().catch(() => {});
      return next;
    });
  };

  return (
    <div className="App">
      {/* 반짝이 하트 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {/* 홈 */}
      {screen === "home" && !showGame && (
        <>
          {homeHearts.map((h) => (
            <img
              key={h.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${h.left}%`,
                width: `${h.size}px`,
                animationDuration: `${h.duration}s`,
              }}
              alt=""
            />
          ))}
          <div className="center-heart-container">
            <img
              src={centerHeart}
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {/* 퀴즈 */}
      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className="window">
            <div className="window-header">
              <div className="window-title">
                💻 퀴즈 게임
                <button className="mute-btn" onClick={toggleMute}>
                  {muted ? "🔇" : "🔊"}
                </button>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGame(false)}>-</button>
                <button onClick={() => setScreen("home")}>❌</button>
              </div>
            </div>
            {!showGame ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={startGame}>
                  🎮 퀴즈 게임 시작
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img
                  src={ground}
                  className="game-background"
                  style={{ left: `${bgOffset}px` }}
                  alt=""
                />
                {quizHearts.map(
                  (h) =>
                    !h.found && (
                      <img
                        key={h.id}
                        src={jujinHeart}
                        className="heart-item"
                        style={{ left: `${h.left + bgOffset}px`, top: h.top }}
                        alt=""
                      />
                    )
                )}
                <img
                  src={fairy}
                  className="fairy-character"
                  style={{ left: fairyX, bottom: fairyY }}
                  alt=""
                />
                {balloonText && (
                  <div className="quiz-balloon" style={{ left: fairyX }}>
                    {balloonText}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
