import React, { useEffect, useState } from "react";
import "./App.css";
import jujinHeart from './image/pkht.png'; // 작은 하트들
import centerHeart from './image/sujin.png'; // 고정 큰 하트
import fairy from './image/fairy.png'; // 요정 캐릭터
import ground from './image/ground.png'; // 픽셀 게임 배경

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      {/* 반짝이 하트들 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {/* 떠다니는 하트들 */}
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          {/* 가운데 고정 하트 + 메시지 */}
          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className="game-container">
            <img src={ground} alt="background" className="game-background" />
            <img src={fairy} alt="fairy" className="fairy-character" />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
