import React, { useEffect, useState } from "react";
import "./App.css";
import jujinHeart from './image/pkht.png'; // ÏûëÏùÄ ÌïòÌä∏Îì§
import centerHeart from './image/sujin.png'; // Í≥†Ï†ï ÌÅ∞ ÌïòÌä∏
import fairy from './image/fairy.png'; // ÏöîÏ†ï Ï∫êÎ¶≠ÌÑ∞
import ground from './image/ground.png'; // ÌîΩÏÖÄ Í≤åÏûÑ Î∞∞Í≤Ω
import hhhh from './image/hhhh.png'; // ÌîΩÏÖÄ Í≤åÏûÑ Î∞∞Í≤Ω


function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      {/* Î∞òÏßùÏù¥ ÌïòÌä∏Îì§ */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {/* Îñ†Îã§ÎãàÎäî ÌïòÌä∏Îì§ */}
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          {/* Í∞ÄÏö¥Îç∞ Í≥†Ï†ï ÌïòÌä∏ + Î©îÏãúÏßÄ */}
          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">ÌïòÌä∏Î•º ÎàåÎü¨Ï§ò</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className="game-window">
            <div className="window-header">üíª ÎØ∏ÎãàÍ≤åÏûÑ</div>
            <div className="game-content">
              <h2>Í≤åÏûÑÏùÑ ÏãúÏûëÌï¥Î≥ºÍπåÏöî?</h2>
              <button className="start-btn">
                <img src={hhhh} alt="start" style={{ width: 30, height: 30 }} />
              </button>
              <img src={fairy} alt="fairy" className="fairy" />

            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
