import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";
import eatSound from "./sounds/game.mp3.wav";
import bgMusicFile from "./sounds/game back.wav";

function App() {
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [fairyX, setFairyX] = useState(100);
  const [fairyY, setFairyY] = useState(0);
  const [bgOffset, setBgOffset] = useState(0);
  const [quizHearts, setQuizHearts] = useState([]);
  const [balloonText, setBalloonText] = useState("");
  const [isJumping, setIsJumping] = useState(false);
  const [isMuted, setIsMuted] = useState(true);

  // refs for audio
  const bgmRef = useRef(null);

  // preload bgm element but do not auto-play
  useEffect(() => {
    bgmRef.current = new Audio(bgMusicFile);
    bgmRef.current.loop = true;
    bgmRef.current.volume = 0.3;
  }, []);

  // generate floating hearts on home
  const [homeHearts, setHomeHearts] = useState([]);
  useEffect(() => {
    setHomeHearts(
      Array.from({ length: 15 }, (_, i) => ({
        id: i,
        left: Math.random() * 100,
        size: 20 + Math.random() * 30,
        duration: 5 + Math.random() * 5,
      }))
    );
  }, []);

  // set up quiz hearts
  useEffect(() => {
    if (!showGameContent) return;
    setQuizHearts(
      Array.from({ length: 5 }, (_, i) => ({
        id: i,
        left: 300 + i * 200,
        top: 250,
        found: false,
      }))
    );
    setFairyX(100);
    setFairyY(0);
    setBgOffset(0);
    setBalloonText("");
    setIsJumping(false);
  }, [showGameContent]);

  // start game handler — only place we call play()
  const handleStartGame = () => {
    // play click sound
    new Audio(startSound).play();
    // then play bgm
    if (!isMuted && bgmRef.current) {
      bgmRef.current.play().catch(() => {});
    }
    setShowGameContent(true);
  };

  // mute toggle
  const toggleMute = () => {
    setIsMuted((m) => {
      const next = !m;
      if (bgmRef.current) {
        if (next) bgmRef.current.pause();
        else bgmRef.current.play().catch(() => {});
      }
      return next;
    });
  };

  // collision
  const checkHeartCollision = (x) => {
    setQuizHearts((prev) =>
      prev.map((h) => {
        if (!h.found && Math.abs(x - (h.left + bgOffset)) < 40) {
          new Audio(eatSound).play();
          setBalloonText("이걸 맞춰봐!");
          return { ...h, found: true };
        }
        return h;
      })
    );
  };

  // movement & jump
  useEffect(() => {
    const onKey = (e) => {
      if (!showGameContent) return;
      let nx = fairyX;
      let nb = bgOffset;
      if (e.key === "ArrowRight") {
        nx += 20;
        nb -= 20;
      } else if (e.key === "ArrowLeft") {
        nx -= 20;
        nb += 20;
      } else if (e.key === " ") {
        if (!isJumping) {
          setIsJumping(true);
          // jump up
          let h = 0;
          const up = setInterval(() => {
            h += 5;
            setFairyY(-h);
            if (h >= 50) {
              clearInterval(up);
              // fall
              const down = setInterval(() => {
                h -= 5;
                setFairyY(h);
                if (h <= 0) {
                  clearInterval(down);
                  setIsJumping(false);
                }
              }, 20);
            }
          }, 20);
        }
      }
      setFairyX(nx);
      setBgOffset(nb);
      checkHeartCollision(nx);
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [fairyX, bgOffset, showGameContent, isJumping]);

  return (
    <div className="App">
      {/* home hearts */}
      {screen === "home" &&
        homeHearts.map((h) => (
          <img
            key={h.id}
            src={jujinHeart}
            className="floating-heart"
            style={{
              left: `${h.left}%`,
              width: `${h.size}px`,
              animationDuration: `${h.duration}s`,
            }}
            alt=""
          />
        ))}

      {screen === "home" && (
        <div className="center-heart-container">
          <img
            src={centerHeart}
            className="center-heart"
            onClick={() => setScreen("quiz")}
          />
          <div className="heart-message">하트를 눌러줘</div>
        </div>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className="window">
            <div className="window-header">
              <div className="window-title">
                💻 퀴즈 게임
                <button className="mute-btn" onClick={toggleMute}>
                  {isMuted ? "🔇" : "🔊"}
                </button>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setScreen("home")}>❌</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  🎮 퀴즈 게임 시작
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img
                  src={ground}
                  className="game-background"
                  style={{ left: `${bgOffset}px` }}
                  alt=""
                />
                {quizHearts.map(
                  (h) =>
                    !h.found && (
                      <img
                        key={h.id}
                        src={jujinHeart}
                        className="heart-item"
                        style={{ left: `${h.left + bgOffset}px`, top: h.top }}
                        alt=""
                      />
                    )
                )}
                <img
                  src={fairy}
                  className="fairy-character"
                  style={{ left: fairyX, bottom: fairyY }}
                  alt=""
                />
                {balloonText && (
                  <div
                    className="quiz-balloon"
                    style={{ left: fairyX }}
                  >
                    {balloonText}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
