import React, { useState, useEffect } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";

function App() {
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);

  const [fairyPos, setFairyPos] = useState({ left: 30, top: 20 });
  const [gameHearts, setGameHearts] = useState([]);
  const [showBalloon, setShowBalloon] = useState(false);

  const playStartSound = () => {
    const audio = new Audio(startSound);
    audio.play();
  };

  const handleStartGame = () => {
    playStartSound();
    setShowGameContent(true);
  };

  // Î∞©Ìñ•ÌÇ§ Ïù¥Îèô Ï≤òÎ¶¨
  useEffect(() => {
    const handleKeyDown = (e) => {
      setFairyPos((prev) => {
        const step = 10;
        let newLeft = prev.left;
        let newTop = prev.top;

        if (e.key === "ArrowLeft") newLeft = Math.max(prev.left - step, 0);
        if (e.key === "ArrowRight") newLeft = Math.min(prev.left + step, 520);
        if (e.key === "ArrowUp") newTop = Math.max(prev.top - step, 0);
        if (e.key === "ArrowDown") newTop = Math.min(prev.top + step, 300);

        return { left: newLeft, top: newTop };
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  // ÌïòÌä∏ ÏúÑÏπò ÏÉùÏÑ±
  useEffect(() => {
    if (showGameContent) {
      const hearts = Array.from({ length: 5 }).map((_, i) => ({
        id: i,
        left: Math.random() * 500,
        top: Math.random() * 300,
        collected: false,
      }));
      setGameHearts(hearts);
    }
  }, [showGameContent]);

  // ÏöîÏ†ïÏù¥ ÌïòÌä∏ÏôÄ Ï∂©ÎèåÌïòÎ©¥ ÌÄ¥Ï¶à ÎßêÌíçÏÑ† ÎùÑÏö∞Í∏∞
  useEffect(() => {
    const updatedHearts = gameHearts.map((heart) => {
      const dx = fairyPos.left - heart.left;
      const dy = fairyPos.top - heart.top;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (!heart.collected && distance < 40) {
        setShowBalloon(true);
        setTimeout(() => setShowBalloon(false), 3000);
        return { ...heart, collected: true };
      }
      return heart;
    });

    setGameHearts(updatedHearts);
  }, [fairyPos]);

  return (
    <div className="App">
      {/* Î∞òÏßùÏù¥ ÌïòÌä∏ Î∞∞Í≤Ω */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {/* Ìôà ÌôîÎ©¥ */}
      {screen === "home" && (
        <>
          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">ÌïòÌä∏Î•º ÎàåÎü¨Ï§ò</div>
          </div>
        </>
      )}

      {/* ÌÄ¥Ï¶à Í≤åÏûÑ ÌôîÎ©¥ */}
      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>ÌÄ¥Ï¶à Í≤åÏûÑ</span>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>üóñ</button>
                <button onClick={() => setScreen("home")}>‚ùå</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  üéÆ ÌÄ¥Ï¶à Í≤åÏûÑ ÏãúÏûë
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img src={ground} className="game-background" alt="bg" />
                {/* ÏöîÏ†ï */}
                <img
                  src={fairy}
                  className="fairy-character"
                  style={{
                    left: `${fairyPos.left}px`,
                    top: `${fairyPos.top}px`,
                  }}
                  alt="fairy"
                />
                {/* ÌïòÌä∏Îì§ */}
                {gameHearts.map(
                  (heart) =>
                    !heart.collected && (
                      <img
                        key={heart.id}
                        src={jujinHeart}
                        className="heart-item"
                        style={{
                          left: `${heart.left}px`,
                          top: `${heart.top}px`,
                        }}
                        alt="heart"
                      />
                    )
                )}
                {/* ÌÄ¥Ï¶à ÎßêÌíçÏÑ† */}
                {showBalloon && (
                  <div
                    className="quiz-balloon"
                    style={{
                      left: `${fairyPos.left + 20}px`,
                      top: `${fairyPos.top - 60}px`,
                    }}
                  >
                    Ïù¥Í±∏ ÎßûÏ∂∞Î¥ê!
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
