import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";
import eatSound from "./sounds/game.mp3.wav";
import bgMusic from "./sounds/game back.wav";

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [fairyX, setFairyX] = useState(100);
  const [fairyY, setFairyY] = useState(0);
  const [bgOffset, setBgOffset] = useState(0);
  const [quizHearts, setQuizHearts] = useState([]);
  const [balloonText, setBalloonText] = useState("");
  const [isJumping, setIsJumping] = useState(false);
  const [isMusicPlaying, setIsMusicPlaying] = useState(true);

  const musicRef = useRef(new Audio(bgMusic));

  useEffect(() => {
    const music = musicRef.current;
    music.loop = true;
    if (isMusicPlaying) {
      music.play();
    } else {
      music.pause();
    }
  }, [isMusicPlaying]);

  const playStartSound = () => new Audio(startSound).play();
  const playEatSound = () => new Audio(eatSound).play();

  const handleStartGame = () => {
    playStartSound();
    setShowGameContent(true);
  };

  const checkHeartCollision = (newX, newBgOffset) => {
    setQuizHearts((prevHearts) =>
      prevHearts.map((heart) => {
        const heartX = heart.left + newBgOffset;
        const diff = Math.abs(newX - heartX);
        if (!heart.found && diff < 40) {
          setBalloonText("이걸 맞춰봐!");
          playEatSound();
          return { ...heart, found: true };
        }
        return heart;
      })
    );
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  useEffect(() => {
    const hearts = Array.from({ length: 5 }, (_, i) => ({
      id: i,
      left: 300 + i * 200,
      top: 250,
      found: false,
    }));
    setQuizHearts(hearts);
    setFairyX(100);
    setBgOffset(0);
    setBalloonText("");
    setFairyY(0);
  }, [showGameContent]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!showGameContent) return;

      let newX = fairyX;
      let newBgOffset = bgOffset;

      if (e.key === "ArrowRight") {
        newX += 20;
        newBgOffset -= 20;
      } else if (e.key === "ArrowLeft") {
        newX -= 20;
        newBgOffset += 20;
      } else if (e.key === " ") {
        if (!isJumping) {
          setIsJumping(true);
          let jumpHeight = 0;
          const jumpInterval = setInterval(() => {
            jumpHeight += 5;
            setFairyY(-jumpHeight);
            if (jumpHeight >= 50) {
              clearInterval(jumpInterval);
              const fallInterval = setInterval(() => {
                jumpHeight -= 5;
                setFairyY(-jumpHeight);
                if (jumpHeight <= 0) {
                  clearInterval(fallInterval);
                  setIsJumping(false);
                }
              }, 20);
            }
          }, 20);
        }
      }

      setFairyX(newX);
      setBgOffset(newBgOffset);
      checkHeartCollision(newX, newBgOffset);
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [fairyX, bgOffset, showGameContent, isJumping]);

  return (
    <div className="App">
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>퀴즈 게임</span>
                <button
                  className="mute-button"
                  onClick={() => setIsMusicPlaying(!isMusicPlaying)}
                >
                  {isMusicPlaying ? "🔊" : "🔇"}
                </button>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>🗖</button>
                <button onClick={() => setScreen("home")}>❌</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  🎮 퀴즈 게임 시작
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img
                  src={ground}
                  className="game-background"
                  style={{ left: `${bgOffset}px` }}
                  alt="bg"
                />
                {quizHearts.map(
                  (heart) =>
                    !heart.found && (
                      <img
                        key={heart.id}
                        src={jujinHeart}
                        className="heart-item"
                        alt="heart"
                        style={{
                          left: `${heart.left + bgOffset}px`,
                          top: `${heart.top}px`,
                        }}
                      />
                    )
                )}
                <img
                  src={fairy}
                  className="fairy-character"
                  alt="fairy"
                  style={{
                    left: `${fairyX}px`,
                    bottom: `${fairyY}px`,
                  }}
                />
                {balloonText && (
                  <div className="quiz-balloon" style={{ left: `${fairyX}px` }}>
                    {balloonText}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
