import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import hhhh from "./image/hhhh.png";

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  const startSoundRef = useRef(null);

  const toggleContent = () => setShowGameContent((prev) => !prev);
  const toggleFullscreen = () => setIsFullscreen((prev) => !prev);

  const playStartSound = () => {
    if (startSoundRef.current) {
      startSoundRef.current.currentTime = 0;
      startSoundRef.current.play();
    }
  };

  const handleStart = () => {
    playStartSound();
    setGameStarted(true);
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      <audio ref={startSoundRef} src="/sounds/start.mp3" preload="auto" />

      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">ÌïòÌä∏Î•º ÎàåÎü¨Ï§ò</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`computer-window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header" onClick={toggleContent}>
              <span className="window-title">üíª ÌÄ¥Ï¶à ÌÉÄÏûÑ</span>
              <div className="window-icons">
                <span className="icon" onClick={toggleContent}>üóï</span>
                <span className="icon" onClick={toggleFullscreen}>üóñ</span>
                <span className="icon" onClick={() => setScreen("home")}>‚ùå</span>
              </div>
            </div>

            {showGameContent ? (
              <div className="game-content">
                <img src={ground} alt="background" className="game-background" />
                <img src={fairy} alt="fairy" className="fairy-character" />
                {!gameStarted && (
                  <button className="heart-start-btn" onClick={handleStart}>
                    <img src={hhhh} alt="start icon" className="start-icon" />
                    <span>ÏãúÏûëÌïòÍ∏∞</span>
                  </button>
                )}
              </div>
            ) : (
              <div className="pink-content">
                <h2 style={{ color: "#fff" }}>üå∏ Îã§Î•∏ ÏΩòÌÖêÏ∏† Îì±Ïû•!</h2>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
