import React, { useEffect, useState } from "react";
import "./App.css";
import jujinHeart from './image/pkht.png';
import centerHeart from './image/sujin.png';
import fairy from './image/fairy.png';
import ground from './image/ground.png';
import hhhh from './image/hhhh.png';

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(true);

  const toggleContent = () => {
    setShowGameContent((prev) => !prev);
  };

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      {/* 반짝이 하트들 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {screen === "home" && (
        <>
          {/* 떠다니는 하트들 */}
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          {/* 가운데 고정 하트 */}
          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className="computer-window">
            <div className="window-header" onClick={toggleContent}>
              💻 미니게임
              <div className="window-icons">
                <span className="icon" onClick={(e) => e.stopPropagation()}>🗕</span>
                <span className="icon" onClick={(e) => e.stopPropagation()}>🗖</span>
                <span className="icon" onClick={(e) => e.stopPropagation()}>❌</span>
              </div>
            </div>

            {showGameContent ? (
              <div className="game-content">
                <img src={ground} alt="background" className="game-background" />
                <img src={fairy} alt="fairy" className="fairy-character" />
                <button className="start-btn">
                  <img src={hhhh} alt="start" style={{ width: 200, height: 200 }} />
                </button>
              </div>
            ) : (
              <div className="pink-content">
                <h2 style={{ color: "#fff" }}>🌸 다른 콘텐츠가 나타났어요!</h2>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
