import React, { useEffect, useState } from "react";
import "./App.css";
import jujinHeart from './image/pkht.png'; // 작은 하트들
import centerHeart from './image/sujin.png'; // 큰 고정 하트 (캐릭터 이미지로도 사용)

// 하트 위치 정보 (퀴즈용)
const initialHearts = [
  { id: 1, x: 200, y: 200, collected: false },
  { id: 2, x: 400, y: 200, collected: false },
];

function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");
  const [playerX, setPlayerX] = useState(100);
  const [quizHearts, setQuizHearts] = useState(initialHearts);
  const [currentQuiz, setCurrentQuiz] = useState(null);
  const [showQuiz, setShowQuiz] = useState(false);

  // 첫 화면용 하트 떠다니기
  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  // 캐릭터 이동 (퀴즈 화면에서)
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (screen !== "quiz") return;
      if (e.key === "ArrowRight") setPlayerX((prev) => prev + 20);
      if (e.key === "ArrowLeft") setPlayerX((prev) => prev - 20);
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [screen]);

  // 퀴즈 하트 충돌 체크
  useEffect(() => {
    if (screen !== "quiz") return;
    quizHearts.forEach((heart) => {
      if (!heart.collected && Math.abs(heart.x - playerX) < 40) {
        setQuizHearts((prev) =>
          prev.map((h) =>
            h.id === heart.id ? { ...h, collected: true } : h
          )
        );
        setCurrentQuiz(`퀴즈 ${heart.id}`);
        setShowQuiz(true);
      }
    });
  }, [playerX, quizHearts, screen]);

  const closeQuiz = () => {
    setShowQuiz(false);
    setCurrentQuiz(null);
  };

  return (
    <div className="App">
      {/* 반짝이 하트들 */}
      {Array.from({ length: 25 }).map((_, i) => (
        <div
          key={i}
          className="sparkle-heart"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
          }}
        />
      ))}

      {/* 홈 화면 */}
      {screen === "home" && (
        <>
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          <div className="center-heart-container">
            <img
              src={centerHeart}
              alt="center heart"
              className="center-heart"
              onClick={() => setScreen("quiz")}
            />
            <div className="heart-message">하트를 눌러줘</div>
          </div>
        </>
      )}

      {/* 퀴즈 미니게임 화면 */}
      {screen === "quiz" && (
        <div className="game-screen">
          <div className="game-area">
            <img
              src={centerHeart}
              alt="character"
              className="player"
              style={{ left: `${playerX}px` }}
            />

            {quizHearts.map((heart) =>
              !heart.collected ? (
                <img
                  key={heart.id}
                  src={jujinHeart}
                  alt="heart"
                  className="heart"
                  style={{ left: `${heart.x}px`, top: `${heart.y}px` }}
                />
              ) : null
            )}

            {showQuiz && (
              <div className="quiz-popup">
                <p>{currentQuiz}</p>
                <button onClick={closeQuiz}>닫기</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
