import React, { useState, useEffect } from "react";
import "./App.css";
import jujinHeart from "./image/pkht.png";
import centerHeart from "./image/sujin.png";
import fairy from "./image/fairy.png";
import ground from "./image/ground.png";
import startSound from "./sounds/onclick.mp3";

const quizList = [
  "ÏöîÏ†ïÏùò Ï≤´ Î≤àÏß∏ ÌÄ¥Ï¶à!", 
  "ÏöîÏ†ïÏùò Îëê Î≤àÏß∏ ÌÄ¥Ï¶à!", 
  "ÏöîÏ†ïÏùò ÏÑ∏ Î≤àÏß∏ ÌÄ¥Ï¶à!", 
  "ÏöîÏ†ïÏùò ÎßàÏßÄÎßâ ÌÄ¥Ï¶à!"
];

function App() {
  const [screen, setScreen] = useState("home");
  const [showGameContent, setShowGameContent] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [fairyPos, setFairyPos] = useState({ x: 30, y: 20 });
  const [heartPositions, setHeartPositions] = useState([]);
  const [currentQuiz, setCurrentQuiz] = useState(0);
  const [showQuizBubble, setShowQuizBubble] = useState(false);

  const playStartSound = () => {
    const audio = new Audio(startSound);
    audio.play();
  };

  const handleStartGame = () => {
    playStartSound();
    setShowGameContent(true);
    generateHearts();
  };

  const generateHearts = () => {
    const positions = [
      { x: 100, y: 100 },
      { x: 200, y: 150 },
      { x: 300, y: 180 },
      { x: 400, y: 120 },
    ];
    setHeartPositions(positions);
  };

  const handleKeyDown = (e) => {
    if (!showGameContent) return;
    let newX = fairyPos.x;
    let newY = fairyPos.y;
    const step = 10;
    if (e.key === "ArrowRight") newX += step;
    if (e.key === "ArrowLeft") newX -= step;
    if (e.key === "ArrowUp") newY -= step;
    if (e.key === "ArrowDown") newY += step;
    setFairyPos({ x: newX, y: newY });

    heartPositions.forEach((pos, index) => {
      const dx = Math.abs(pos.x - newX);
      const dy = Math.abs(pos.y - newY);
      if (dx < 30 && dy < 30) {
        const newHearts = [...heartPositions];
        newHearts.splice(index, 1);
        setHeartPositions(newHearts);
        setShowQuizBubble(true);
        setTimeout(() => {
          setShowQuizBubble(false);
          setCurrentQuiz((prev) => prev + 1);
        }, 3000);
      }
    });
  };

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });

  return (
    <div className="App">
      {screen === "home" && (
        <div className="center-heart-container">
          <img
            src={centerHeart}
            alt="center"
            className="center-heart"
            onClick={() => setScreen("quiz")}
          />
          <div className="heart-message">ÌïòÌä∏Î•º ÎàåÎü¨Ï§ò</div>
        </div>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <div className={`window ${isFullscreen ? "fullscreen" : ""}`}>
            <div className="window-header">
              <div className="window-title">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Windows_XP_Icon.png"
                  alt="icon"
                  className="win-icon"
                />
                <span>ÌÄ¥Ï¶à Í≤åÏûÑ</span>
              </div>
              <div className="window-controls">
                <button onClick={() => setShowGameContent(false)}>-</button>
                <button onClick={() => setIsFullscreen(!isFullscreen)}>üóñ</button>
                <button onClick={() => setScreen("home")}>‚ùå</button>
              </div>
            </div>

            {!showGameContent ? (
              <div className="pink-content">
                <h2 className="start-text" onClick={handleStartGame}>
                  üéÆ ÌÄ¥Ï¶à Í≤åÏûÑ ÏãúÏûë
                </h2>
              </div>
            ) : (
              <div className="game-content">
                <img src={ground} className="game-background" alt="bg" />
                <img
                  src={fairy}
                  className="fairy-character"
                  alt="fairy"
                  style={{ left: fairyPos.x, bottom: fairyPos.y }}
                />
                {heartPositions.map((pos, i) => (
                  <img
                    key={i}
                    src={jujinHeart}
                    className="floating-heart"
                    style={{ left: pos.x, bottom: pos.y, position: "absolute" }}
                    alt="heart"
                  />
                ))}
                {showQuizBubble && (
                  <div
                    className="quiz-bubble"
                    style={{ left: fairyPos.x + 40, bottom: fairyPos.y + 80 }}
                  >
                    {quizList[currentQuiz]}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
