import React, { useEffect, useState } from "react";
import "./App.css";
import jujinHeart from './image/pkht.png'; // ÏûëÏùÄ ÌïòÌä∏Îì§
import centerHeart from './image/sujin.png'; // ÌÅ∞ Í≥†Ï†ï ÌïòÌä∏

// ‚úÖ Ïù¥ Ï§ÑÏù¥ Íº≠ ÌïÑÏöîÌï¥!
function App() {
  const [hearts, setHearts] = useState([]);
  const [screen, setScreen] = useState("home");

  useEffect(() => {
    const newHearts = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: 20 + Math.random() * 30,
      duration: 5 + Math.random() * 5,
    }));
    setHearts(newHearts);
  }, []);

  return (
    <div className="App">
      <div className="sparkle-background"></div> {/* Î∞òÏßùÏù¥ Î∞∞Í≤Ω */}

      {screen === "home" && (
        <>
          {/* Îë•Ïã§Îë•Ïã§ Îñ†Îã§ÎãàÎäî ÌïòÌä∏Îì§ */}
          {hearts.map((heart) => (
            <img
              key={heart.id}
              src={jujinHeart}
              className="floating-heart"
              style={{
                left: `${heart.left}%`,
                width: `${heart.size}px`,
                animationDuration: `${heart.duration}s`,
              }}
              alt="heart"
            />
          ))}

          {/* Í≥†Ï†ïÎêú ÌÅ∞ ÌïòÌä∏ */}
          <img
            src={centerHeart}
            alt="center heart"
            className="center-heart"
            onClick={() => setScreen("quiz")}
          />
        </>
      )}

      {screen === "quiz" && (
        <div className="quiz-screen">
          <h1>ÌÄ¥Ï¶à ÌôîÎ©¥Ïù¥ÏóêÏöî üí°</h1>
        </div>
      )}
    </div>
  );
}

// Ïù¥ Ï§ÑÏùÄ Í∑∏ÎåÄÎ°ú Îë¨!
export default App;
